import prettier from 'prettier';
import XString from "./XmlString";

export default (svgString: string = ''): string => {
	const xml = new XString(svgString);
  const defaultSize = xml.getSize('height');
  const defaultColor = xml.getColor(xml.isWithStroke() ? 'stroke' : 'fill');
  xml
		.setSize('width', '{props.size}')
		.setSize('height', '{props.size}')
		.setColor('fill', '{props.color}')
		.setColor('stroke', '{props.color}');
	const str = `
	/**
 * @author Auto Generated by IconGarden
 */

  import { defineComponent } from 'vue-next';
 
 export default defineComponent({
   name: 'icon-',
   props: {
     size: {
       type: Number,
       default: ${defaultSize},
     },
     color: {
      type: String,
      default: '${defaultColor}',
     }
   },
   setup: (props) => {
     return (
       ${xml.toString()}
     );
   }
 })
	`;
	return prettier.format(str, {
		parser: 'babel',
		tabWidth: 4,
	});
};